import Attribute from './attribute';

/**
The value of this attribute is always a boolean. Null values are coerced to false.

String attributes can be queries using `equal` and `not`. Matching on
`greaterThan` and `lessThan` is not supported.
*/
export default class AttributeBoolean extends Attribute {
  toJSON(val) {
    return val;
  }
  fromJSON(val) {
    return ((val === 'true') || (val === true)) || false;
  }
  columnSQL() {
    return `${this.jsonKey} INTEGER`;
  }
}

import Attribute from './attribute';
import Matcher from './matcher';

/**
Collection attributes provide basic support for one-to-many relationships.
For example, Threads in N1 have a collection of Labels or Folders.

When Collection attributes are marked as `queryable`, the RxDatabase
automatically creates a join table and maintains it as you create, save,
and delete models. When you call `persistModel`, entries are added to the
join table associating the ID of the model with the IDs of models in the collection.

Collection attributes have an additional clause builder, `contains`:

```coffee
db.findAll(Thread).where([Thread.attributes.categories.contains('inbox')])
```

This is equivalent to writing the following SQL:

```sql
SELECT `Thread`.`data` FROM `Thread`
INNER JOIN `ThreadLabel` AS `M1` ON `M1`.`id` = `Thread`.`id`
WHERE `M1`.`value` = 'inbox'
ORDER BY `Thread`.`last_message_received_timestamp` DESC
```

The value of this attribute is always an array of other model objects.

Section: Database
*/
export default class AttributeCollection extends Attribute {
  constructor({modelKey, jsonKey, itemClass, joinOnField, joinQueryableBy, queryable}) {
    super({modelKey, jsonKey, queryable});
    this.ItemClass = this.itemClass = itemClass;
    this.joinOnField = joinOnField;
    this.joinQueryableBy = joinQueryableBy || [];
  }

  toJSON(vals) {
    if (!vals) {
      return [];
    }

    if (!(vals instanceof Array)) {
      throw new Error(`AttributeCollection::toJSON: ${this.modelKey} is not an array.`);
    }

    const json = []
    for (const val of vals) {
      if (!(val instanceof this.ItemClass)) {
        throw new Error(`AttributeCollection::toJSON: Value \`${val}\` in ${this.modelKey} is not an ${this.ItemClass.name}`);
      }
      if (val.toJSON !== undefined) {
        json.push(val.toJSON());
      } else {
        json.push(val);
      }
    }
    return json;
  }

  fromJSON(json) {
    if (!json || !(json instanceof Array)) {
      return [];
    }
    const objs = [];

    for (const objJSON of json) {
      // Note: It's possible for a malformed API request to return an array
      // of null values. N1 is tolerant to this type of error, but shouldn't
      // happen on the API end.
      if (!objJSON) {
        continue;
      }

      if (this.ItemClass.prototype.fromJSON) {
        const obj = new this.ItemClass();
        // Important: if no ids are in the JSON, don't make them up
        // randomly.  This causes an object to be "different" each time it's
        // de-serialized even if it's actually the same, makes React
        // components re-render!
        obj.id = undefined;
        obj.fromJSON(objJSON);
        objs.push(obj);
      } else {
        const obj = new this.ItemClass(objJSON);
        obj.id = undefined;
        objs.push(obj);
      }
    }
    return objs;
  }

  /**
  @returns {Matcher} - Matcher for objects containing the provided value.
  */
  contains(val) {
    this._assertPresentAndQueryable('contains', val);
    return new Matcher(this, 'contains', val);
  }

  containsAny(vals) {
    this._assertPresentAndQueryable('contains', vals);
    return new Matcher(this, 'containsAny', vals);
  }
}

import Attribute from './attribute';
import Matcher from './matcher';

/**
The value of this attribute is always a Javascript `Date`, or `null`.
*/
export default class AttributeDateTime extends Attribute {
  toJSON(val) {
    if (!val) {
      return null;
    }
    if (!(val instanceof Date)) {
      throw new Error(`Attempting to toJSON AttributeDateTime which is not a date: ${this.modelKey} = ${val}`);
    }
    return val.getTime() / 1000.0;
  }

  fromJSON(val) {
    return val ? new Date(val * 1000) : null;
  }

  columnSQL() {
    return `${this.jsonKey} INTEGER`;
  }

  /**
  @returns {Matcher} - Matcher for objects greater than the provided value.
  */
  greaterThan(val) {
    this._assertPresentAndQueryable('greaterThan', val);
    return new Matcher(this, '>', val)
  }

  /**
  @returns {Matcher} - Matcher for objects less than the provided value.
  */
  lessThan(val) {
    this._assertPresentAndQueryable('lessThan', val);
    return new Matcher(this, '<', val);
  }

  /**
  @returns {Matcher} - Matcher for objects greater than or equal to the provided value.
  */
  greaterThanOrEqualTo(val) {
    this._assertPresentAndQueryable('greaterThanOrEqualTo', val);
    return new Matcher(this, '>=', val);
  }

  /**
  @returns {Matcher} - Matcher for objects less than or equal to the provided value.
  */
  lessThanOrEqualTo(val) {
    this._assertPresentAndQueryable('lessThanOrEqualTo', val);
    return new Matcher(this, '<=', val);
  }

  gt = AttributeDateTime.greaterThan;
  lt = AttributeDateTime.lessThan;
  gte = AttributeDateTime.greaterThanOrEqualTo;
  lte = AttributeDateTime.lessThanOrEqualTo;
}

import Attribute from './attribute';

const NullPlaceholder = "!NULLVALUE!";

/**
Joined Data attributes allow you to store certain attributes of an
object in a separate table in the database. We use this attribute
type for Message bodies. Storing message bodies, which can be very
large, in a separate table allows us to make queries on message
metadata extremely fast, and inflate Message objects without their
bodies to build the thread list.

When building a query on a model with a JoinedData attribute, you need
to call `include` to explicitly load the joined data attribute.
The query builder will automatically perform a `LEFT OUTER JOIN` with
the secondary table to retrieve the attribute:

```coffee
db.find(Message, '123').then (message) ->
  // message.body is undefined

db.find(Message, '123').include(Message.attributes.body).then (message) ->
  // message.body is defined
```

When you call `persistModel`, JoinedData attributes are automatically
written to the secondary table.

JoinedData attributes cannot be `queryable`.

Section: Database
*/
export default class AttributeJoinedData extends Attribute {
  static NullPlaceholder = NullPlaceholder;

  constructor({modelKey, jsonKey, modelTable, queryable}) {
    super({modelKey, jsonKey, queryable});
    this.modelTable = modelTable;
  }

  toJSON(val) {
    return val;
  }

  fromJSON(val) {
    return (val === null || val === undefined || val === false) ? null : `${val}`;
  }

  selectSQL() {
    // NullPlaceholder is necessary because if the LEFT JOIN returns nothing, it leaves the field
    // blank, and it comes through in the result row as "" rather than NULL
    return `IFNULL(\`${this.modelTable}\`.\`value\`, '${NullPlaceholder}') AS \`${this.modelKey}\``;
  }

  includeSQL(klass) {
    return `LEFT OUTER JOIN \`${this.modelTable}\` ON \`${this.modelTable}\`.\`id\` = \`${klass.name}\`.\`id\``;
  }
}

import Attribute from './attribute';
import Matcher from './matcher';

/**
The value of this attribute is always a number, or null.
*/
export default class AttributeNumber extends Attribute {
  toJSON(val) {
    return val;
  }

  fromJSON(val) {
    return isNaN(val) ? null : Number(val);
  }

  columnSQL() {
    return `${this.jsonKey} INTEGER`;
  }

  // Public: Returns a {Matcher} for objects greater than the provided value.
  greaterThan(val) {
    this._assertPresentAndQueryable('greaterThan', val);
    return new Matcher(this, '>', val);
  }

  // Public: Returns a {Matcher} for objects less than the provided value.
  lessThan(val) {
    this._assertPresentAndQueryable('lessThan', val);
    return new Matcher(this, '<', val);
  }

  // Public: Returns a {Matcher} for objects greater than the provided value.
  greaterThanOrEqualTo(val) {
    this._assertPresentAndQueryable('greaterThanOrEqualTo', val);
    return new Matcher(this, '>=', val);
  }

  // Public: Returns a {Matcher} for objects less than the provided value.
  lessThanOrEqualTo(val) {
    this._assertPresentAndQueryable('lessThanOrEqualTo', val);
    return new Matcher(this, '<=', val);
  }

  gt = AttributeNumber.prototype.greaterThan;
  lt = AttributeNumber.prototype.lessThan;
  gte = AttributeNumber.prototype.greaterThanOrEqualTo;
  lte = AttributeNumber.prototype.lessThanOrEqualTo;
}

import Attribute from './attribute';

/**
The value of this attribute is always an object that can be cast to `itemClass`
*/
export default class AttributeObject extends Attribute {
  constructor({modelKey, jsonKey, itemClass, queryable}) {
    super({modelKey, jsonKey, queryable});
    this.ItemClass = itemClass;
  }

  toJSON(val) {
    return (val && val.toJSON) ? val.toJSON() : val;
  }

  fromJSON(val) {
    if (!this.ItemClass) {
      return val || "";
    }
    const obj = new this.ItemClass(val);

    // Important: if no ids are in the JSON, don't make them up randomly.
    // This causes an object to be "different" each time it's de-serialized
    // even if it's actually the same, makes React components re-render!
    obj.id = undefined;

    // Warning: typeof(null) is object
    if (obj.fromJSON && !!val && (typeof val === 'object')) {
      obj.fromJSON(val);
    }

    return obj;
  }
}

import Attribute from './attribute';
import Matcher from './matcher';

/**
The value of this attribute is always a string or `null`.

String attributes can be queries using `equal`, `not`, and `startsWith`.
Matching on `greaterThan` and `lessThan` is not supported.
*/
export default class AttributeString extends Attribute {
  toJSON(val) {
    return val;
  }

  fromJSON(val) {
    return (val === null || val === undefined || val === false) ? null : `${val}`;
  }

  // Public: Returns a {Matcher} for objects starting with the provided value.
  startsWith(val) {
    return new Matcher(this, 'startsWith', val);
  }

  columnSQL() {
    return `${this.jsonKey} TEXT`;
  }

  like(val) {
    this._assertPresentAndQueryable('like', val);
    return new Matcher(this, 'like', val);
  }
}

import Matcher from './matcher';
import SortOrder from './sort-order';

/**
The Attribute class represents a single model attribute, like 'account_id'.
Subclasses of {Attribute} like {AttributeDateTime} know how to covert between
the JSON representation of that type and the javascript representation.
The Attribute class also exposes convenience methods for generating {Matcher} objects.
*/
export default class Attribute {
  constructor({modelKey, queryable, jsonKey}) {
    this.modelKey = modelKey;
    this.jsonKey = jsonKey || modelKey;
    this.queryable = queryable;
  }

  _assertPresentAndQueryable(fnName, val) {
    if (val === undefined) {
      throw new Error(`Attribute::${fnName} (${this.modelKey}) - you must provide a value`);
    }
    if (!this.queryable) {
      throw new Error(`Attribute::${fnName} (${this.modelKey}) - this field cannot be queried against`);
    }
  }

  /**
  @param val - The attribute value
  @returns {Matcher} - Matcher for objects `=` to the provided value.
  */
  equal(val) {
    this._assertPresentAndQueryable('equal', val);
    return new Matcher(this, '=', val);
  }

  /**
  @param {Array} val - An array of values
  @returns {Matcher} - Matcher for objects in the provided array.
  */
  in(val) {
    this._assertPresentAndQueryable('in', val);

    if (!(val instanceof Array)) {
      throw new Error(`Attribute.in: you must pass an array of values.`);
    }
    if (val.length === 0) {
      console.warn(`Attribute::in (${this.modelKey}) called with an empty set. You should avoid this useless query!`);
    }
    return (val.length === 1) ? new Matcher(this, '=', val[0]) : new Matcher(this, 'in', val);
  }

  /**
  @param val - The attribute value
  @returns {Matcher} - A matcher for objects `!=` to the provided value.
  */
  not(val) {
    this._assertPresentAndQueryable('not', val);
    return new Matcher(this, '!=', val);
  }

  /**
  @returns {SortOrder} - Returns a descending sort order for this attribute
  */
  descending() {
    return new SortOrder(this, 'DESC');
  }

  /**
  @returns {SortOrder} - Returns an ascending sort order for this attribute
  */
  ascending() {
    return new SortOrder(this, 'ASC');
  }

  toJSON(val) {
    return val;
  }

  fromJSON(val) {
    return val || null;
  }
}

import {tableNameForJoin} from '../utils';

// https://www.sqlite.org/faq.html#q14
// That's right. Two single quotes in a row
const singleQuoteEscapeSequence = "''";

// https://www.sqlite.org/fts5.html#section_3
const doubleQuoteEscapeSequence = '""';


/**
The Matcher class encapsulates a particular comparison clause on an {@link Attribute}.
Matchers can evaluate whether or not an object matches them, and also compose
SQL clauses for the {@link RxDatabase}. Each matcher has a reference to a model
attribute, a comparator and a value. This class is heavily inspired by
NSPredicate on Mac OS X / CoreData.

```js

// Retrieving Matchers

const isUnread = Thread.attributes.unread.equal(true);

const hasLabel = Thread.attributes.categories.contains('label-id-123');

// Using Matchers in Database Queries

const db.findAll(Thread).where(isUnread)...

// Using Matchers to test Models

const threadA = new Thread({unread: true})
const threadB = new Thread({unread: false})

isUnread.evaluate(threadA)
// => true

isUnread.evaluate(threadB)
// => false

```
*/
class Matcher {
  constructor(attr, comparator, val) {
    this.attr = attr;
    this.comparator = comparator;
    this.val = val;

    this.muid = Matcher.muid;
    Matcher.muid = (Matcher.muid + 1) % 50;
  }

  attribute() {
    return this.attr;
  }

  value() {
    return this.val;
  }

  evaluate(model) {
    let modelValue = model[this.attr.modelKey];
    if (modelValue instanceof Function) {
      modelValue = modelValue()
    }
    const matcherValue = this.val;

    // Given an array of strings or models, and a string or model search value,
    // will find if a match exists.
    const modelArrayContainsValue = (array, searchItem) => {
      const asId = (v) => ((v && v.id) ? v.id : v);
      const search = asId(searchItem)
      for (const item of array) {
        if (asId(item) === search) {
          return true;
        }
      }
      return false;
    }

    switch (this.comparator) {
      case '=':
        return modelValue === matcherValue
      case '<':
        return modelValue < matcherValue
      case '>':
        return modelValue > matcherValue
      case '<=':
        return modelValue <= matcherValue
      case '>=':
        return modelValue >= matcherValue
      case 'in':
        return matcherValue.includes(modelValue)
      case 'contains':
        return modelArrayContainsValue(modelValue, matcherValue)
      case 'containsAny':
        return !!matcherValue.find((submatcherValue) => modelArrayContainsValue(modelValue, submatcherValue))
      case 'startsWith':
        return modelValue.startsWith(matcherValue)
      case 'like':
        return modelValue.search(new RegExp(`.*${matcherValue}.*`, "gi")) >= 0
      default:
        throw new Error(`Matcher.evaulate() not sure how to evaluate ${this.attr.modelKey} with comparator ${this.comparator}`)
    }
  }

  joinTableRef() {
    return `M${this.muid}`;
  }

  joinSQL(klass) {
    switch (this.comparator) {
      case 'contains':
      case 'containsAny': {
        const joinTable = tableNameForJoin(klass, this.attr.itemClass);
        const joinTableRef = this.joinTableRef();
        return `INNER JOIN \`${joinTable}\` AS \`${joinTableRef}\` ON \`${joinTableRef}\`.\`id\` = \`${klass.name}\`.\`id\``;
      }
      default:
        return false;
    }
  }

  whereSQL(klass) {
    const val = (this.comparator === "like") ? `%${this.val}%` : this.val;
    let escaped = null;

    if (typeof val === 'string') {
      escaped = `'${val.replace(/'/g, singleQuoteEscapeSequence)}'`;
    } else if (val === true) {
      escaped = 1
    } else if (val === false) {
      escaped = 0
    } else if (val instanceof Date) {
      escaped = val.getTime() / 1000
    } else if (val instanceof Array) {
      const escapedVals = []
      for (const v of val) {
        if (typeof v !== 'string') {
          throw new Error(`${this.attr.jsonKey} value ${v} must be a string.`);
        }
        escapedVals.push(`'${v.replace(/'/g, singleQuoteEscapeSequence)}'`);
      }
      escaped = `(${escapedVals.join(',')})`;
    } else {
      escaped = val;
    }

    switch (this.comparator) {
      case 'startsWith':
        return " RAISE `TODO`; ";
      case 'contains':
        return `\`${this.joinTableRef()}\`.\`value\` = ${escaped}`;
      case 'containsAny':
        return `\`${this.joinTableRef()}\`.\`value\` IN ${escaped}`;
      default:
        return `\`${klass.name}\`.\`${this.attr.jsonKey}\` ${this.comparator} ${escaped}`;
    }
  }
}

Matcher.muid = 0

/**
This subclass is publicly exposed as Matcher.Or.
@private
*/
class OrCompositeMatcher extends Matcher {
  constructor(children) {
    super();
    this.children = children;
  }

  attribute() {
    return null;
  }

  value() {
    return null;
  }

  evaluate(model) {
    return this.children.some((matcher) => matcher.evaluate(model));
  }

  joinSQL(klass) {
    const joins = []
    for (const matcher of this.children) {
      const join = matcher.joinSQL(klass);
      if (join) {
        joins.push(join);
      }
    }
    return (joins.length) ? joins.join(" ") : false;
  }

  whereSQL(klass) {
    const wheres = this.children.map((matcher) => matcher.whereSQL(klass));
    return `(${wheres.join(" OR ")})`;
  }
}

/**
This subclass is publicly exposed as Matcher.And.
@private
*/
class AndCompositeMatcher extends Matcher {
  constructor(children) {
    super();
    this.children = children;
  }

  attribute() {
    return null;
  }

  value() {
    return null;
  }

  evaluate(model) {
    return this.children.every((m) => m.evaluate(model));
  }

  joinSQL(klass) {
    const joins = []
    for (const matcher of this.children) {
      const join = matcher.joinSQL(klass);
      if (join) {
        joins.push(join);
      }
    }
    return joins;
  }

  whereSQL(klass) {
    const wheres = this.children.map((m) => m.whereSQL(klass));
    return `(${wheres.join(" AND ")})`;
  }
}

/**
This subclass is publicly exposed as Matcher.Not.
@private
*/
class NotCompositeMatcher extends AndCompositeMatcher {
  whereSQL(klass) {
    return `NOT (${super.whereSQL(klass)})`;
  }
}

class SearchMatcher extends Matcher {
  constructor(searchQuery) {
    super(null, null, null);
    this.searchQuery = (
      searchQuery.trim()
      .replace(/^['"]/, "")
      .replace(/['"]$/, "")
      .replace(/'/g, singleQuoteEscapeSequence)
      .replace(/"/g, doubleQuoteEscapeSequence)
    )
  }

  attribute() {
    return null;
  }

  value() {
    return null
  }

  // The only way to truly check if a model matches this matcher is to run the query
  // again and check if the model is in the results. This is too expensive, so we
  // will always return true so models aren't excluded from the
  // SearchQuerySubscription result set
  evaluate() {
    return true;
  }

  joinSQL(klass) {
    const searchTable = `${klass.name}Search`
    const joinTableRef = this.joinTableRef()
    return `INNER JOIN \`${searchTable}\` AS \`${joinTableRef}\` ON \`${joinTableRef}\`.\`content_id\` = \`${klass.name}\`.\`id\``;
  }

  whereSQL(klass) {
    const searchTable = `${klass.name}Search`
    return `\`${searchTable}\` MATCH '"${this.searchQuery}"*'`;
  }
}

Matcher.Or = OrCompositeMatcher
Matcher.And = AndCompositeMatcher
Matcher.Not = NotCompositeMatcher
Matcher.Search = SearchMatcher

export default Matcher;

