/* eslint global-require:0 */
/* eslint import/newline-after-import:0 */
import Model from './model';
import {tableNameForJoin, registeredObjectReplacer} from './utils';

import Attributes from './attributes';
import DatabaseChangeRecord from './database-change-record';

require('promise.prototype.finally').shim();
require('promise.try').shim()

const {AttributeCollection, AttributeJoinedData} = Attributes;

/**
DatabaseTransaction exposes a convenient API for querying and modifying an RxDB
within a SQLite transaction.

You shouldn't need to instantiate this class directly. Instead, use
RxDatabase#inTransaction.
*/
export default class DatabaseTransaction {
  constructor(database) {
    this.database = database;
    this._changeRecords = [];
    this._opened = false;
  }

  /**
  @borrows RxDatabase#find
  */
  find(...args) { return this.database.find(...args) }
  findBy(...args) { return this.database.findBy(...args) }
  findAll(...args) { return this.database.findAll(...args) }
  modelify(...args) { return this.database.modelify(...args) }
  count(...args) { return this.database.count(...args) }
  findJSONBlob(...args) { return this.database.findJSONBlob(...args) }

  execute(fn) {
    if (this._opened) {
      throw new Error("DatabaseTransaction:execute was already called");
    }

    return this._query("BEGIN IMMEDIATE TRANSACTION").then(() => {
      this._opened = true;
      return fn(this);
    }).finally(() => {
      if (!this._opened) {
        return null;
      }
      this._opened = false;
      return this._query("COMMIT").then(() => {
        this.database.transactionDidCommitChanges(this._changeRecords);
      });
    });
  }

  // Mutating the Database

  persistJSONBlob(id, json) {
    const JSONBlob = require('./json-blob').default;
    return this.persistModel(new JSONBlob({id, json}));
  }

  /**
  Asynchronously writes `model` to the cache and triggers a change event.

  @param {Model} model - A {Model} to write to the database.

  @returns {Promise} - A promise that:
    - resolves after the database queries are complete and any listening
      database callbacks have finished
    - rejects if any databse query fails or one of the triggering
      callbacks failed
  */
  persistModel(model) {
    if (!model || !(model instanceof Model)) {
      throw new Error("DatabaseTransaction::persistModel - You must pass an instance of the Model class.");
    }
    return this.persistModels([model]);
  }

  /**
  Asynchronously writes `models` to the cache and triggers a single change
  event. Note: Models must be of the same class to be persisted in a batch operation.

  @param {Array} models - An {Array} of {Model} objects to write to the database.

  @returns {Promise} - A promise that:
    - resolves after the database queries are complete and any listening
      database callbacks have finished
    - rejects if any databse query fails or one of the triggering
      callbacks failed
  */
  persistModels(models = []) {
    if (models.length === 0) {
      return Promise.resolve();
    }

    const klass = models[0].constructor;
    const clones = [];
    const ids = {};

    if (!(models[0] instanceof Model)) {
      throw new Error(`DatabaseTransaction::persistModels - You must pass an array of items which descend from the Model class.`);
    }

    for (const model of models) {
      if (!model || (model.constructor !== klass)) {
        throw new Error(`DatabaseTransaction::persistModels - When you batch persist objects, they must be of the same type`);
      }
      if (ids[model.id]) {
        throw new Error(`DatabaseTransaction::persistModels - You must pass an array of models with different ids. ID ${model.id} is in the set multiple times.`)
      }
      clones.push(model.clone());
      ids[model.id] = true;
    }

    // Note: It's important that we clone the objects since other code could mutate
    // them during the save process. We want to guaruntee that the models you send to
    // persistModels are saved exactly as they were sent.
    const metadata = {
      objectClass: clones[0].constructor.name,
      objectIds: Object.keys(ids),
      objects: clones,
      type: 'persist',
    };

    return this._runMutationHooks('beforeDatabaseChange', metadata).then((data) => {
      return this._writeModels(clones).then(() => {
        this._runMutationHooks('afterDatabaseChange', metadata, data);
        return this._changeRecords.push(new DatabaseChangeRecord(metadata));
      });
    });
  }

  /**
  Asynchronously removes `model` from the cache and triggers a change event.

  @param {Model} model - A {Model} to write to the database.

  @returns {Promise} - A promise that
    - resolves after the database queries are complete and any listening
      database callbacks have finished
    - rejects if any databse query fails or one of the triggering
      callbacks failed
  */
  unpersistModel(model) {
    const clone = model.clone();
    const metadata = {
      objectClass: clone.constructor.name,
      objectIds: [clone.id],
      objects: [clone],
      type: 'unpersist',
    }

    return this._runMutationHooks('beforeDatabaseChange', metadata).then((data) => {
      return this._deleteModel(clone).then(() => {
        this._runMutationHooks('afterDatabaseChange', metadata, data);
        return this._changeRecords.push(new DatabaseChangeRecord(metadata));
      });
    });
  }

  // PRIVATE METHODS ////////////////////////////////////////////////////////

  _query = (...args) => {
    return this.database._query(...args);
  }

  _runMutationHooks(selectorName, metadata, data = []) {
    const beforePromises = this.database.mutationHooks().map((hook, idx) =>
      Promise.try(() => hook[selectorName](this._query, metadata, data[idx]))
    );

    return Promise.all(beforePromises).catch((e) => {
      if (!process.env.CI) {
        console.warn(`DatabaseTransaction Hook: ${selectorName} failed`, e);
      }
      return Promise.resolve([]);
    });
  }

  // Fires the queries required to write models to the DB
  //
  // Returns a promise that:
  //   - resolves when all write queries are complete
  //   - rejects if any query fails
  _writeModels(models) {
    const promises = [];

    // IMPORTANT: This method assumes that all the models you
    // provide are of the same class, and have different ids!

    // Avoid trying to write too many objects a time - sqlite can only handle
    // value sets `(?,?)...` of less than SQLITE_MAX_COMPOUND_SELECT (500),
    // and we don't know ahead of time whether we'll hit that or not.
    if (models.length > 50) {
      return Promise.all([
        this._writeModels(models.slice(0, 50)),
        this._writeModels(models.slice(50)),
      ]);
    }

    const klass = models[0].constructor;
    const attributes = Object.keys(klass.attributes).map(key => klass.attributes[key])

    const columnAttributes = attributes.filter((attr) =>
      attr.queryable && attr.columnSQL && attr.jsonKey !== 'id'
    );

    // Compute the columns in the model table and a question mark string
    const columns = ['id', 'data'];
    const columnMarks = ['?', '?'];
    columnAttributes.forEach((attr) => {
      columns.push(attr.jsonKey);
      columnMarks.push('?');
    });
    const columnsSQL = columns.join(',');
    const marksSet = `(${columnMarks.join(',')})`;

    // Prepare a batch insert VALUES (?,?,?), (?,?,?)... by assembling
    // an array of the values and a corresponding question mark set
    const values = [];
    const marks = [];
    const ids = [];
    const modelsJSONs = [];
    for (const model of models) {
      const json = model.toJSON({joined: false});
      modelsJSONs.push(json);
      ids.push(model.id);
      values.push(model.id, JSON.stringify(json, registeredObjectReplacer));
      columnAttributes.forEach((attr) => {
        values.push(json[attr.jsonKey]);
      });
      marks.push(marksSet);
    }

    const marksSQL = marks.join(',');

    promises.push(this._query(`REPLACE INTO \`${klass.name}\` (${columnsSQL}) VALUES ${marksSQL}`, values));

    // For each join table property, find all the items in the join table for this
    // model and delete them. Insert each new value back into the table.
    const collectionAttributes = attributes.filter((attr) =>
      attr.queryable && attr instanceof AttributeCollection
    )

    collectionAttributes.forEach((attr) => {
      const joinTable = tableNameForJoin(klass, attr.itemClass);

      promises.push(this._query(`DELETE FROM \`${joinTable}\` WHERE \`id\` IN ('${ids.join("','")}')`));

      const joinMarks = [];
      const joinedValues = [];
      const joinMarkUnit = `(${["?", "?"].concat(attr.joinQueryableBy.map(() => '?')).join(',')})`;
      const joinQueryableByJSONKeys = attr.joinQueryableBy.map(joinedModelKey =>
        klass.attributes[joinedModelKey].jsonKey
      );
      const joinColumns = ['id', 'value'].concat(joinQueryableByJSONKeys);

      // https://www.sqlite.org/limits.html: SQLITE_MAX_VARIABLE_NUMBER
      const valuesPerRow = joinColumns.length;
      const rowsPerInsert = Math.floor(600 / valuesPerRow);
      const valuesPerInsert = rowsPerInsert * valuesPerRow;

      models.forEach((model, idx) => {
        const joinedModels = model[attr.modelKey] || [];
        for (const joined of joinedModels) {
          if (!attr.joinOnField) {
            throw new Error(`Queryable collection attribute ${attr.modelKey} must specify a joinOnField`);
          }
          const joinValue = joined[attr.joinOnField];
          joinMarks.push(joinMarkUnit);
          joinedValues.push(model.id, joinValue);
          for (const joinedJsonKey of joinQueryableByJSONKeys) {
            joinedValues.push(modelsJSONs[idx][joinedJsonKey]);
          }
        }
      });

      if (joinedValues.length !== 0) {
        // Write no more than 200 items (400 values) at once to avoid sqlite limits
        // 399 values: slices:[0..0]
        // 400 values: slices:[0..0]
        // 401 values: slices:[0..1]
        const slicePageCount = Math.ceil(joinMarks.length / rowsPerInsert) - 1;
        for (let slice = 0; slice <= slicePageCount; slice++) {
          const [ms, me] = [slice * rowsPerInsert, slice * rowsPerInsert + rowsPerInsert];
          const [vs, ve] = [slice * valuesPerInsert, slice * valuesPerInsert + valuesPerInsert];
          promises.push(this._query(`INSERT OR IGNORE INTO \`${joinTable}\` (\`${joinColumns.join('`,`')}\`) VALUES ${joinMarks.slice(ms, me).join(',')}`, joinedValues.slice(vs, ve)));
        }
      }
    });

    // For each joined data property stored in another table...
    const joinedDataAttributes = attributes.filter(attr =>
      attr instanceof AttributeJoinedData
    )

    joinedDataAttributes.forEach((attr) => {
      for (const model of models) {
        if (model[attr.modelKey] !== undefined) {
          promises.push(this._query(`REPLACE INTO \`${attr.modelTable}\` (\`id\`, \`value\`) VALUES (?, ?)`, [model.id, model[attr.modelKey]]));
        }
      }
    });

    return Promise.all(promises);
  }

  // Fires the queries required to delete models to the DB
  //
  // Returns a promise that:
  //   - resolves when all deltion queries are complete
  //   - rejects if any query fails
  _deleteModel(model) {
    const promises = []

    const klass = model.constructor;
    const attributes = Object.keys(klass.attributes).map(key => klass.attributes[key]);

    // Delete the primary record
    promises.push(this._query(`DELETE FROM \`${klass.name}\` WHERE \`id\` = ?`, [model.id]))

    // For each join table property, find all the items in the join table for this
    // model and delte them. Insert each new value back into the table.
    const collectionAttributes = attributes.filter(attr =>
      attr.queryable && attr instanceof AttributeCollection
    );

    collectionAttributes.forEach((attr) => {
      const joinTable = tableNameForJoin(klass, attr.itemClass);
      promises.push(this._query(`DELETE FROM \`${joinTable}\` WHERE \`id\` = ?`, [model.id]))
    });

    const joinedDataAttributes = attributes.filter(attr =>
      attr instanceof AttributeJoinedData
    );

    joinedDataAttributes.forEach((attr) => {
      promises.push(this._query(`DELETE FROM \`${attr.modelTable}\` WHERE \`id\` = ?`, [model.id]));
    });

    return Promise.all(promises);
  }
}

import Model from './model';
import Query from './query';
import Attributes from './attributes';

class JSONBlobQuery extends Query {
  formatResult(objects) {
    return objects[0] ? objects[0].json : null;
  }
}

export default class JSONBlob extends Model {
  static Query = JSONBlobQuery;

  static attributes = {
    id: Attributes.String({
      queryable: true,
      modelKey: 'id',
    }),

    json: Attributes.Object({
      modelKey: 'json',
      jsonKey: 'json',
    }),
  };

  get key() {
    return this.id;
  }

  set key(val) {
    this.id = val;
  }
}

import Attributes from './attributes';

function generateTempId() {
  const s4 = () => Math.floor((1 + Math.random()) * 0x10000).toString(16).substring(1);
  return `local-${s4()}${s4()}-${s4()}`;
}
/**
A base class for RxDB models that provides abstract support for JSON
serialization and deserialization, and attribute-based matching.

Your RxDB data classes should extend Model and extend it's attributes:

- {AttributeString} id: The resolved canonical ID of the model used in the
database and generally throughout the app.

*/
export default class Model {

  static attributes = {
    id: Attributes.String({
      queryable: true,
      modelKey: 'id',
    }),
  }

  static naturalSortOrder() {
    return null;
  }

  constructor(values = {}) {
    for (const key of Object.keys(this.constructor.attributes)) {
      this[key] = values[key];
    }
    this.id = this.id || generateTempId();
  }

  clone() {
    return (new this.constructor()).fromJSON(this.toJSON())
  }

  // Public: Returns an {Array} of {Attribute} objects defined on the Model's constructor
  //
  attributes() {
    return Object.assign({}, this.constructor.attributes)
  }

  // Public: Inflates the model object from JSON, using the defined attributes to
  // guide type coercision.
  //
  // - `json` A plain Javascript {Object} with the JSON representation of the model.
  //
  // This method is chainable.
  //
  fromJSON(json) {
    // Note: The loop in this function has been optimized for the V8 'fast case'
    // https://github.com/petkaantonov/bluebird/wiki/Optimization-killers
    //
    for (const key of Object.keys(this.constructor.attributes)) {
      const attr = this.constructor.attributes[key];
      const attrValue = json[attr.jsonKey];
      if (attrValue !== undefined) {
        this[key] = attr.fromJSON(attrValue);
      }
    }
    return this;
  }

  // Public: Deflates the model to a plain JSON object. Only attributes defined
  // on the model are included in the JSON.
  //
  // - `options` (optional) An {Object} with additional options. To skip joined
  //    data attributes in the toJSON representation, pass the `joined:false`
  //
  // Returns an {Object} with the JSON representation of the model.
  //
  toJSON(options = {}) {
    const json = {}
    for (const key of Object.keys(this.constructor.attributes)) {
      const attr = this.constructor.attributes[key];
      const attrValue = this[key];

      if (attrValue === undefined) {
        continue;
      }
      if (attr instanceof Attributes.AttributeJoinedData && (options.joined === false)) {
        continue;
      }
      json[attr.jsonKey] = attr.toJSON(attrValue);
    }
    return json;
  }

  toString() {
    return JSON.stringify(this.toJSON());
  }

  // Public: Evaluates the model against one or more {Matcher} objects.
  //
  // - `criteria` An {Array} of {Matcher}s to run on the model.
  //
  // Returns true if the model matches the criteria.
  //
  matches(criteria) {
    if (!(criteria instanceof Array)) {
      return false;
    }
    for (const matcher of criteria) {
      if (!matcher.evaluate(this)) {
        return false;
      }
    }
    return true;
  }
}

import QueryResultSet from './query-result-set';
import AttributeJoinedData from './attributes/attribute-joined-data';

// TODO: Make mutator methods QueryResultSet.join(), QueryResultSet.clip...
export default class MutableQueryResultSet extends QueryResultSet {

  immutableClone() {
    const set = new QueryResultSet({
      _ids: [].concat(this._ids),
      _modelsHash: Object.assign({}, this._modelsHash),
      _query: this._query,
      _offset: this._offset,
    });
    Object.freeze(set._ids);
    Object.freeze(set._modelsHash);
    return set;
  }

  clipToRange(range) {
    if (range.isInfinite()) {
      return;
    }
    if (range.offset > this._offset) {
      this._ids = this._ids.slice(range.offset - this._offset);
      this._offset = range.offset;
    }

    const rangeEnd = range.offset + range.limit;
    const selfEnd = this._offset + this._ids.length;
    if (rangeEnd < selfEnd) {
      this._ids.length = Math.max(0, rangeEnd - this._offset);
    }

    const models = this.models();
    this._modelsHash = {};
    this._idToIndexHash = null;
    models.forEach((m) => this.updateModel(m));
  }

  addModelsInRange(rangeModels, range) {
    this.addIdsInRange(rangeModels.map(m => m.id), range);
    rangeModels.forEach((m) => this.updateModel(m));
  }

  addIdsInRange(rangeIds, range) {
    if ((this._offset === null) || range.isInfinite()) {
      this._ids = rangeIds;
      this._idToIndexHash = null;
      this._offset = range.offset;
    } else {
      const currentEnd = this._offset + this._ids.length;
      const rangeIdsEnd = range.offset + rangeIds.length;

      if (rangeIdsEnd < this._offset) {
        throw new Error(`addIdsInRange: You can only add adjacent values (${rangeIdsEnd} < ${this._offset})`)
      }
      if (range.offset > currentEnd) {
        throw new Error(`addIdsInRange: You can only add adjacent values (${range.offset} > ${currentEnd})`);
      }

      let existingBefore = []
      if (range.offset > this._offset) {
        existingBefore = this._ids.slice(0, range.offset - this._offset);
      }

      let existingAfter = []
      if ((rangeIds.length === range.limit) && (currentEnd > rangeIdsEnd)) {
        existingAfter = this._ids.slice(rangeIdsEnd - this._offset);
      }

      this._ids = [].concat(existingBefore, rangeIds, existingAfter);
      this._idToIndexHash = null;
      this._offset = Math.min(this._offset, range.offset);
    }
  }

  updateModel(item) {
    if (!item) {
      return;
    }

    // Sometimes the new copy of `item` doesn't contain the joined data present
    // in the old one, since it's not provided by default and may not have changed.
    // Make sure we never drop joined data by pulling it over.
    const existing = this._modelsHash[item.id];
    if (existing) {
      const attrs = existing.constructor.attributes
      for (const key of Object.keys(attrs)) {
        const attr = attrs[key];
        if ((attr instanceof AttributeJoinedData) && (item[attr.modelKey] === undefined)) {
          item[attr.modelKey] = existing[attr.modelKey];
        }
      }
    }
    this._modelsHash[item.id] = item;
    this._idToIndexHash = null;
  }

  removeModelAtOffset(item, offset) {
    const idx = offset - this._offset;
    delete this._modelsHash[item.id];
    this._ids.splice(idx, 1);
    this._idToIndexHash = null;
  }

  setQuery(query) {
    this._query = query.clone();
    this._query.finalize();
  }
}

import QuerySubscription from './query-subscription'

class MutableQuerySubscription extends QuerySubscription {

  replaceQuery(nextQuery) {
    if (this._query && this._query.sql() === nextQuery.sql()) {
      return
    }

    let rangeIsOnlyChange = false;
    if (this._query) {
      rangeIsOnlyChange = this._query.clone().offset(0).limit(0).sql() === nextQuery.clone().offset(0).limit(0).sql()
    }

    this.cancelPendingUpdate()

    nextQuery.finalize()
    this._query = nextQuery
    if (!(this._set && rangeIsOnlyChange)) {
      this._set = null
    }
    this.update()
  }

  replaceRange = ({start, end}) => {
    if (!this._query) {
      return
    }

    const next = this._query.clone().page(start, end);
    if (!next.range().isEqual(this._query.range())) {
      this.replaceQuery(next);
    }
  }
}

export default MutableQuerySubscription

/**
QueryRange represents a LIMIT + OFFSET pair and provides high-level methods
for comparing, copying and  joining ranges. It also provides syntax sugar
around infinity (the absence of a defined LIMIT or OFFSET).
*/
export default class QueryRange {
  static infinite() {
    return new QueryRange({limit: null, offset: null});
  }

  static rangeWithUnion(a, b) {
    if (a.isInfinite() || b.isInfinite()) {
      return QueryRange.infinite();
    }
    if (!a.isContiguousWith(b)) {
      throw new Error('You cannot union ranges which do not touch or intersect.');
    }

    return new QueryRange({
      start: Math.min(a.start, b.start),
      end: Math.max(a.end, b.end),
    });
  }

  static rangesBySubtracting(a, b) {
    if (!b) {
      return [];
    }

    if (a.isInfinite() || b.isInfinite()) {
      throw new Error("You cannot subtract infinite ranges.");
    }

    const uncovered = []
    if (b.start > a.start) {
      uncovered.push(new QueryRange({start: a.start, end: Math.min(a.end, b.start)}))
    }
    if (b.end < a.end) {
      uncovered.push(new QueryRange({start: Math.max(a.start, b.end), end: a.end}));
    }
    return uncovered;
  }

  get start() {
    return this.offset;
  }

  get end() {
    return this.offset + this.limit;
  }

  constructor({limit, offset, start, end} = {}) {
    this.limit = limit;
    this.offset = offset;

    if ((start !== undefined) && (offset === undefined)) {
      this.offset = start;
    }
    if ((end !== undefined) && (limit === undefined)) {
      this.limit = end - this.offset;
    }

    if (this.limit === undefined) {
      throw new Error("You must specify a limit");
    }
    if (this.offset === undefined) {
      throw new Error("You must specify an offset");
    }
  }

  clone() {
    const {limit, offset} = this;
    return new QueryRange({limit, offset});
  }

  isInfinite() {
    return (this.limit === null) && (this.offset === null);
  }

  isEqual(b) {
    return (this.start === b.start) && (this.end === b.end);
  }

  // Returns true if joining the two ranges would not result in empty space.
  // ie: they intersect or touch
  isContiguousWith(b) {
    if (this.isInfinite() || b.isInfinite()) {
      return true;
    }
    return ((this.start <= b.start) && (b.start <= this.end)) || ((this.start <= b.end) && (b.end <= this.end));
  }

  toString() {
    return `QueryRange{${this.start} - ${this.end}}`;
  }
}

import QueryRange from './query-range';

/**
Instances of QueryResultSet hold a set of models retrieved from the database
for a given query and offset.

Complete vs Incomplete:

QueryResultSet keeps an array of item ids and a lookup table of models.
The lookup table may be incomplete if the QuerySubscription isn't finished
preparing results. You can use `isComplete` to determine whether the set
has every model.

Offset vs Index:

To avoid confusion, "index" (used within the implementation) refers to an item's
position in an array, and "offset" refers to it's position in the query
result set. For example, an item might be at index 20 in the _ids array, but
at offset 120 in the result.
*/
export default class QueryResultSet {

  static setByApplyingModels(set, models) {
    if (models instanceof Array) {
      throw new Error("setByApplyingModels: A hash of models is required.");
    }
    const out = set.clone();
    out._modelsHash = models;
    out._idToIndexHash = null;
    return out;
  }

  constructor(other = {}) {
    this._offset = (other._offset !== undefined) ? other._offset : null;
    this._query = (other._query !== undefined) ? other._query : null;
    this._idToIndexHash = (other._idToIndexHash !== undefined) ? other._idToIndexHash : null;
    // Clone, since the others may be frozen
    this._modelsHash = Object.assign({}, other._modelsHash || {})
    this._ids = [].concat(other._ids || []);
  }

  clone() {
    return new this.constructor({
      _ids: [].concat(this._ids),
      _modelsHash: Object.assign({}, this._modelsHash),
      _idToIndexHash: Object.assign({}, this._idToIndexHash),
      _query: this._query,
      _offset: this._offset,
    });
  }

  /**
  @returns {Boolean} - True if every model in the result set is available, false
    if part of the set is still being loaded. (Usually following range changes.)
  */
  isComplete() {
    return this._ids.every((id) => this._modelsHash[id]);
  }

  /**
  @returns {QueryRange} - The represented range.
  */
  range() {
    return new QueryRange({offset: this._offset, limit: this._ids.length});
  }

  /**
  @returns {Query} - The represented query.
  */
  query() {
    return this._query;
  }

  /**
  @returns {QueryRange} - The number of items in the represented range. Note
  that a range (`LIMIT 10 OFFSET 0`) may return fewer than the maximum number
  of items if none match the query.
  */
  count() {
    return this._ids.length;
  }

  /**
  @returns {Boolean} - True if the result set is empty, false otherwise.
  */
  empty() {
    return this.count() === 0;
  }

  /**
  @returns {Array} - the model IDs in the represented result.
  */
  ids() {
    return this._ids;
  }

  /**
  @param {Number} offset - The desired offset.
  @returns {Array} - the model ID available at the requested offset, or undefined.
  */
  idAtOffset(offset) {
    return this._ids[offset - this._offset];
  }

  /**
  @returns {Array} - An array of model objects. If the result is not yet complete,
  this array may contain `undefined` values.
  */
  models() {
    return this._ids.map((id) => this._modelsHash[id]);
  }

  /**
  @protected
  */
  modelCacheCount() {
    return Object.keys(this._modelsHash).length;
  }

  /**
  @param {Number} offset - The desired offset.
  @returns {Model} - the model at the requested offset.
  */
  modelAtOffset(offset) {
    if (!Number.isInteger(offset)) {
      throw new Error("QueryResultSet.modelAtOffset() takes a numeric index. Maybe you meant modelWithId()?");
    }
    return this._modelsHash[this._ids[offset - this._offset]];
  }

  /**
  @param {String} id - The desired ID.
  @returns {Model} - the model with the requested ID, or undefined.
  */
  modelWithId(id) {
    return this._modelsHash[id];
  }

  _buildIdToIndexHash() {
    this._idToIndexHash = {}
    this._ids.forEach((id, idx) => {
      this._idToIndexHash[id] = idx;
      const model = this._modelsHash[id];
      if (model) {
        this._idToIndexHash[model.id] = idx;
      }
    });
  }

  /**
  @param {String} id - The desired ID.
  @returns {Number} - the offset of `ID`, relative to all of the query results.
  */
  offsetOfId(id) {
    if (this._idToIndexHash === null) {
      this._buildIdToIndexHash();
    }

    if (this._idToIndexHash[id] === undefined) {
      return -1;
    }
    return this._idToIndexHash[id] + this._offset
  }
}

/* eslint global-require: 0 */
import QuerySubscription from './query-subscription';

/**
The QuerySubscriptionPool maintains a list of all of the query
subscriptions in the app. In the future, this class will monitor performance,
merge equivalent subscriptions, etc.

@private
*/
export default class QuerySubscriptionPool {
  constructor(database) {
    this._database = database;
    this._subscriptions = {};
    this._cleanupChecks = [];
    this._setup();
  }

  add(query, callback) {
    // TODO
    // if (NylasEnv.inDevMode()) {
    //   callback._registrationPoint = this._formatRegistrationPoint((new Error).stack);
    // }

    const key = this._keyForQuery(query);
    let subscription = this._subscriptions[key];
    if (!subscription) {
      subscription = new QuerySubscription(query);
      this._subscriptions[key] = subscription;
    }

    subscription.addCallback(callback);
    return () => {
      subscription.removeCallback(callback);
      this._scheduleCleanupCheckForSubscription(key);
    };
  }

  addPrivateSubscription(key, subscription, callback) {
    this._subscriptions[key] = subscription;
    subscription.addCallback(callback);
    return () => {
      subscription.removeCallback(callback);
      this._scheduleCleanupCheckForSubscription(key);
    };
  }

  printSubscriptions() {
    // TODO
    // if (!NylasEnv.inDevMode()) {
    //   console.log("printSubscriptions is only available in developer mode.");
    //   return;
    // }

    for (const key of Object.keys(this._subscriptions)) {
      const subscription = this._subscriptions[key];
      console.log(key);
      console.group();
      for (const callback of subscription._callbacks) {
        console.log(`${callback._registrationPoint}`);
      }
      console.groupEnd();
    }
  }

  _scheduleCleanupCheckForSubscription(key) {
    // We unlisten / relisten to lots of subscriptions and setTimeout is actually
    // /not/ that fast. Create one timeout for all checks, not one for each.
    if (this._cleanupChecks.length === 0) {
      setTimeout(() => this._runCleanupChecks(), 1);
    }
    this._cleanupChecks.push(key);
  }

  _runCleanupChecks() {
    for (const key of this._cleanupChecks) {
      const subscription = this._subscriptions[key];
      if (subscription && (subscription.callbackCount() === 0)) {
        delete this._subscriptions[key];
      }
    }
    this._cleanupChecks = [];
  }

  _formatRegistrationPoint(stackString) {
    const stack = stackString.split('\n');
    let ii = 0;
    let seenRx = false;
    while (ii < stack.length) {
      const hasRx = stack[ii].indexOf('rx.lite') !== -1;
      seenRx = seenRx || hasRx;
      if (seenRx === true && !hasRx) {
        break;
      }
      ii += 1;
    }

    return stack.slice(ii, ii + 4).join('\n');
  }

  _keyForQuery(query) {
    return query.sql();
  }

  _setup() {
    this._database.listen(this._onChange);
  }

  _onChange = (record) => {
    for (const key of Object.keys(this._subscriptions)) {
      const subscription = this._subscriptions[key];
      subscription.applyChangeRecord(record);
    }
  }
}

import QueryRange from './query-range';
import MutableQueryResultSet from './mutable-query-result-set';

export default class QuerySubscription {
  constructor(query, options = {}) {
    this._query = query;
    this._options = options;

    this._set = null;
    this._callbacks = [];
    this._lastResult = null;
    this._updateInFlight = false;
    this._queuedChangeRecords = [];
    this._queryVersion = 1;

    if (this._query) {
      if (this._query._count) {
        throw new Error("QuerySubscription::constructor - You cannot listen to count queries.")
      }

      this._query.finalize();

      if (this._options.initialModels) {
        this._set = new MutableQueryResultSet();
        this._set.addModelsInRange(this._options.initialModels, new QueryRange({
          limit: this._options.initialModels.length,
          offset: 0,
        }));
        this._createResultAndTrigger();
      } else {
        this.update();
      }
    }
  }

  query = () => {
    return this._query;
  }

  addCallback = (callback) => {
    if (!(callback instanceof Function)) {
      throw new Error(`QuerySubscription:addCallback - expects a function, received ${callback}`);
    }
    this._callbacks.push(callback);

    if (this._lastResult) {
      process.nextTick(() => {
        if (!this._lastResult) { return; }
        callback(this._lastResult);
      });
    }
  }

  hasCallback = (callback) => {
    return (this._callbacks.indexOf(callback) !== -1);
  }

  removeCallback(callback) {
    if (!(callback instanceof Function)) {
      throw new Error(`QuerySubscription:removeCallback - expects a function, received ${callback}`)
    }
    this._callbacks = this._callbacks.filter(c => c !== callback);
    if (this.callbackCount() === 0) {
      this.onLastCallbackRemoved()
    }
  }

  onLastCallbackRemoved() {

  }

  callbackCount = () => {
    return this._callbacks.length;
  }

  applyChangeRecord = (record) => {
    if (!this._query || record.objectClass !== this._query.objectClass()) {
      return;
    }
    if (record.objects.length === 0) {
      return;
    }

    this._queuedChangeRecords.push(record);
    if (!this._updateInFlight) {
      this._processChangeRecords();
    }
  }

  cancelPendingUpdate = () => {
    this._queryVersion += 1;
    this._updateInFlight = false;
  }

  // Scan through change records and apply them to the last result set.
  _processChangeRecords = () => {
    if (this._queuedChangeRecords.length === 0) {
      return;
    }
    if (!this._set) {
      this.update();
      return;
    }

    let knownImpacts = 0;
    let unknownImpacts = 0;

    this._queuedChangeRecords.forEach((record) => {
      if (record.type === 'unpersist') {
        for (const item of record.objects) {
          const offset = this._set.offsetOfId(item.id)
          if (offset !== -1) {
            this._set.removeModelAtOffset(item, offset);
            unknownImpacts += 1;
          }
        }
      } else if (record.type === 'persist') {
        for (const item of record.objects) {
          const offset = this._set.offsetOfId(item.id);
          const itemIsInSet = offset !== -1;
          const itemShouldBeInSet = item.matches(this._query.matchers());

          if (itemIsInSet && !itemShouldBeInSet) {
            this._set.removeModelAtOffset(item, offset)
            unknownImpacts += 1
          } else if (itemShouldBeInSet && !itemIsInSet) {
            this._set.updateModel(item)
            unknownImpacts += 1;
          } else if (itemIsInSet) {
            const oldItem = this._set.modelWithId(item.id);
            this._set.updateModel(item);

            if (this._itemSortOrderHasChanged(oldItem, item)) {
              unknownImpacts += 1
            } else {
              knownImpacts += 1
            }
          }
        }
        // If we're not at the top of the result set, we can't be sure whether an
        // item previously matched the set and doesn't anymore, impacting the items
        // in the query range. We need to refetch IDs to be sure our set === correct.
        if ((this._query.range().offset > 0) && (unknownImpacts + knownImpacts) < record.objects.length) {
          unknownImpacts += 1
        }
      }
    });

    this._queuedChangeRecords = [];

    if (unknownImpacts > 0) {
      this.update({mustRefetchEntireRange: true});
    } else if (knownImpacts > 0) {
      this._createResultAndTrigger();
    }
  }

  _itemSortOrderHasChanged(old, updated) {
    for (const descriptor of this._query.orderSortDescriptors()) {
      const oldSortValue = old[descriptor.attr.modelKey];
      const updatedSortValue = updated[descriptor.attr.modelKey];

      // http://stackoverflow.com/questions/4587060/determining-date-equality-in-javascript
      if (!(oldSortValue >= updatedSortValue && oldSortValue <= updatedSortValue)) {
        return true;
      }
    }
    return false;
  }

  update({mustRefetchEntireRange} = {}) {
    this._updateInFlight = true;

    const desiredRange = this._query.range();
    const currentRange = this._set ? this._set.range() : null;
    const hasNonInfiniteRange = currentRange && !currentRange.isInfinite() && !desiredRange.isInfinite();

    // If we have a limited range, and changes don't require that we refetch
    // the entire range, just fetch the missing items. This is the path typically
    // used while scrolling.
    if (hasNonInfiniteRange && !mustRefetchEntireRange) {
      const missingRange = this._getMissingRange(desiredRange, currentRange);
      this._fetchRange(missingRange, {
        version: this._queryVersion,
        fetchEntireModels: true,
      });
    } else {
      const haveNoModels = !this._set || this._set.modelCacheCount() === 0;
      this._fetchRange(desiredRange, {
        version: this._queryVersion,
        fetchEntireModels: haveNoModels,
      });
    }
  }

  _getMissingRange = (desiredRange, currentRange) => {
    if (currentRange && !currentRange.isInfinite() && !desiredRange.isInfinite()) {
      const ranges = QueryRange.rangesBySubtracting(desiredRange, currentRange);
      return (ranges.length === 1) ? ranges[0] : desiredRange;
    }
    return desiredRange;
  }

  _getQueryForRange = (range, fetchEntireModels) => {
    let rangeQuery = null;
    if (!range.isInfinite()) {
      rangeQuery = rangeQuery || this._query.clone();
      rangeQuery.offset(range.offset).limit(range.limit);
    }
    if (!fetchEntireModels) {
      rangeQuery = rangeQuery || this._query.clone();
      rangeQuery.idsOnly();
    }
    rangeQuery = rangeQuery || this._query;
    return rangeQuery;
  }

  _fetchRange(range, {version, fetchEntireModels}) {
    const rangeQuery = this._getQueryForRange(range, fetchEntireModels);

    this._query._database.run(rangeQuery, {format: false}).then((results) => {
      if (this._queryVersion !== version) {
        return;
      }

      if (this._set && !this._set.range().isContiguousWith(range)) {
        this._set = null;
      }
      this._set = this._set || new MutableQueryResultSet();

      if (fetchEntireModels) {
        this._set.addModelsInRange(results, range);
      } else {
        this._set.addIdsInRange(results, range);
      }

      this._set.clipToRange(this._query.range());

      this._fetchMissingModels().then((models) => {
        if (this._queryVersion !== version) {
          return;
        }
        for (const m of models) {
          this._set.updateModel(m);
        }
        this._createResultAndTrigger();
      });
    });
  }

  _fetchMissingModels() {
    const missingIds = this._set.ids().filter(id => !this._set.modelWithId(id));
    if (missingIds.length === 0) {
      return Promise.resolve([]);
    }
    return this._query._database.findAll(this._query._klass, {id: missingIds});
  }

  _createResultAndTrigger = () => {
    const allCompleteModels = this._set.isComplete()
    const uniqueSeen = {};
    let uniqueCount = 0;
    for (const i of this._set.ids()) {
      if (!uniqueSeen[i]) {
        uniqueCount += 1;
      }
      uniqueSeen[i] = true;
    }

    const allUniqueIds = uniqueCount === this._set.ids().length

    let error = null;
    if (!allCompleteModels) {
      error = new Error("QuerySubscription: Applied all changes and result set is missing models.");
    }
    if (!allUniqueIds) {
      error = new Error("QuerySubscription: Applied all changes and result set contains duplicate IDs.");
    }

    if (error) {
      // TODO NylasEnv.reportError(error);
      this._set = null;
      this.update();
      return;
    }

    if (this._options.emitResultSet) {
      this._set.setQuery(this._query);
      this._lastResult = this._set.immutableClone();
    } else {
      this._lastResult = this._query.formatResult(this._set.models());
    }

    this._callbacks.forEach((callback) => callback(this._lastResult));

    // process any additional change records that have arrived
    if (this._updateInFlight) {
      this._updateInFlight = false;
      this._processChangeRecords();
    }
  }
}

/* eslint global-require: 0 */
/* eslint import/newline-after-import: 0 */
import Rx from 'rx-lite';
import Attributes from './attributes';
import QueryRange from './query-range';
import {registeredObjectReviver, tableNameForJoin} from './utils';

const {Matcher, AttributeJoinedData, AttributeCollection} = Attributes;

/**
ModelQuery exposes an ActiveRecord-style syntax for building database queries
that return models and model counts. Model queries are returned from the factory methods
{RxDatabase::find}, {RxDatabase::findBy}, {RxDatabase::findAll},
and {RxDatabase::count}, and are the primary interface for retrieving data
from the app's local cache.

ModelQuery does not allow you to modify the local cache. To create, update or
delete items from the local cache, see {RxDatabase::inTransaction}
and {DatabaseTransaction::persistModel}.

**Simple Example:** Fetch a thread

```js
const query = db.find(Thread, '123a2sc1ef4131');
query.then((thread) {
  // thread or null
});
```

**Advanced Example:** Fetch 50 threads in the inbox, in descending order

```coffee
const query = db.findAll(Thread);
query.where([Thread.attributes.categories.contains('label-id')])
     .order([Thread.attributes.lastMessageReceivedTimestamp.descending()])
     .limit(100).offset(50)
     .then((threads) {
  // array of threads
});
```
*/
export default class ModelQuery {

  /**
  @param {Model} class - A {Model} class to query
  @param {RxDatabase} [database] - An optional reference to a {RxDatabase}
    the query will be executed on.
  */
  constructor(klass, database) {
    this._klass = klass;
    this._database = database || require('./rx-database').default;
    this._matchers = [];
    this._orders = [];
    this._distinct = false;
    this._range = QueryRange.infinite();
    this._returnOne = false;
    this._returnIds = false;
    this._includeJoinedData = [];
    this._count = false;
  }

  /**
  @returns {Query} - A deep copy of the Query that can be modified.
  */
  clone() {
    const q = new ModelQuery(this._klass, this._database).where(this._matchers).order(this._orders);
    q._orders = [].concat(this._orders);
    q._includeJoinedData = [].concat(this._includeJoinedData);
    q._range = this._range.clone();
    q._distinct = this._distinct;
    q._returnOne = this._returnOne;
    q._returnIds = this._returnIds;
    q._count = this._count;
    return q;
  }

  distinct() {
    this._distinct = true;
    return this;
  }

  /**
  Add one or more where clauses to the query
  This method is chainable.

  @param {Array} matchers - Array of {Matcher} objects that add where clauses
    to the underlying query.
  */
  where(matchers) {
    this._assertNotFinalized();

    if (matchers instanceof Matcher) {
      this._matchers.push(matchers);
    } else if (matchers instanceof Array) {
      for (const m of matchers) {
        if (!(m instanceof Matcher)) {
          throw new Error("You must provide instances of `Matcher`");
        }
      }
      this._matchers = this._matchers.concat(matchers);
    } else if (matchers instanceof Object) {
      // Support a shorthand format of {id: '123', accountId: '123'}
      for (const key of Object.keys(matchers)) {
        const value = matchers[key];
        const attr = this._klass.attributes[key];
        if (!attr) {
          const msg = `Cannot create where clause \`${key}:${value}\`. ${key} is not an attribute of ${this._klass.name}`;
          throw new Error(msg);
        }

        if (value instanceof Array) {
          this._matchers.push(attr.in(value));
        } else {
          this._matchers.push(attr.equal(value));
        }
      }
    }
    return this;
  }

  whereAny(matchers) {
    this._assertNotFinalized();
    this._matchers.push(new Matcher.Or(matchers));
    return this;
  }

  search(query) {
    this._assertNotFinalized();
    this._matchers.push(new Matcher.Search(query));
    return this;
  }

  /**
  Include specific joined data attributes in result objects.

  @param {AttributeJoinedData} attr - Attribute that you want to be populated in
  the returned models. Note: This results in a LEFT OUTER JOIN.
  See {AttributeJoinedData} for more information.

  This method is chainable.
  */
  include(attr) {
    this._assertNotFinalized();
    if (!(attr instanceof AttributeJoinedData)) {
      throw new Error("query.include() must be called with a joined data attribute");
    }
    this._includeJoinedData.push(attr);
    return this;
  }

  /**
  Include all of the available joined data attributes in returned models.

  This method is chainable.
  */
  includeAll() {
    this._assertNotFinalized()
    for (const key of Object.keys(this._klass.attributes)) {
      const attr = this._klass.attributes[key];
      if (attr instanceof AttributeJoinedData) {
        this.include(attr);
      }
    }
    return this;
  }

  /**
  Apply a sort order to the query.

  @param {Array} orders - An {Array} of one or more {SortOrder} objects that
    determine the sort order of returned models.

  This method is chainable.
  */
  order(ordersOrOrder) {
    this._assertNotFinalized();
    const orders = (ordersOrOrder instanceof Array) ? ordersOrOrder : [ordersOrOrder];
    this._orders = this._orders.concat(orders);
    return this;
  }

  /**
  Set the `singular` flag - only one model will be returned from the
  query, and a `LIMIT 1` clause will be used.

  This method is chainable.
  */
  one() {
    this._assertNotFinalized();
    this._returnOne = true;
    return this;
  }

  /**
  Limit the number of query results.

  @param {Number} limit - The number of models that should be returned.

  This method is chainable.
  */
  limit(limit) {
    this._assertNotFinalized()
    if (this._returnOne && limit > 1) {
      throw new Error("Cannot use limit > 1 with one()");
    }
    this._range = this._range.clone();
    this._range.limit = limit;
    return this;
  }

  /**
  @param {Number} offset - The start offset of the query.
  This method is chainable.
  */
  offset(offset) {
    this._assertNotFinalized();
    this._range = this._range.clone();
    this._range.offset = offset;
    return this;
  }

  /**
  A convenience method for setting both limit and offset given a desired page size.
  */
  page(start, end, pageSize = 50, pagePadding = 100) {
    const roundToPage = (n) => Math.max(0, Math.floor(n / pageSize) * pageSize)
    this.offset(roundToPage(start - pagePadding));
    this.limit(roundToPage((end - start) + pagePadding * 2));
    return this;
  }

  /**
  Set the `count` flag - instead of returning inflated models,
  the query will return the result `COUNT`.

  This method is chainable.
  */
  count() {
    this._assertNotFinalized();
    this._count = true;
    return this;
  }

  idsOnly() {
    this._assertNotFinalized();
    this._returnIds = true;
    return this;
  }

  // Query Execution

  /**
  Short-hand syntax that calls run().then(fn) with the provided function.

  @returns {Promise} - A promise that resolves with the Models returned by the
  query, or rejects with an error from the Database layer.
  */
  then(next) {
    return this.run(this).then(next);
  }

  /**
  @returns {Promise} - A Promise that resolves with the Models returned by the
  query, or rejects with an error from the Database layer.
  */
  run() {
    return this._database.run(this);
  }

  inflateResult(result) {
    if (!result) {
      return null;
    }

    if (this._count) {
      return result[0].count / 1;
    }
    if (this._returnIds) {
      return result.map(row => row.id);
    }

    try {
      return result.map((row) => {
        const json = JSON.parse(row.data, registeredObjectReviver)
        const object = (new this._klass()).fromJSON(json);
        for (const attr of this._includeJoinedData) {
          let value = row[attr.jsonKey];
          if (value === AttributeJoinedData.NullPlaceholder) {
            value = null;
          }
          object[attr.modelKey] = value;
        }
        return object;
      });
    } catch (jsonError) {
      throw new Error(`Query could not parse the database result. Query: ${this.sql()}, Error: ${jsonError.toString()}`);
    }
  }

  formatResult(inflated) {
    if (this._returnOne) {
      return inflated[0];
    }
    if (this._count) {
      return inflated;
    }
    return [].concat(inflated);
  }

  // Query SQL Building

  /**
  @returns {String} - The SQL generated for the query.
  */
  sql() {
    this.finalize();

    let result = null;

    if (this._count) {
      result = `COUNT(*) as count`;
    } else if (this._returnIds) {
      result = `\`${this._klass.name}\`.\`id\``;
    } else {
      result = `\`${this._klass.name}\`.\`data\``;
      this._includeJoinedData.forEach((attr) => {
        result += `, ${attr.selectSQL(this._klass)} `;
      })
    }

    const order = this._count ? '' : this._orderClause();

    let limit = '';
    if (Number.isInteger(this._range.limit)) {
      limit = `LIMIT ${this._range.limit}`;
    } else {
      limit = ''
    }
    if (Number.isInteger(this._range.offset)) {
      limit += ` OFFSET ${this._range.offset}`;
    }

    const distinct = this._distinct ? ' DISTINCT' : '';
    const allMatchers = this.matchersFlattened();

    const joins = allMatchers.filter((matcher) => matcher.attr instanceof AttributeCollection)

    if ((joins.length === 1) && this._canSubselectForJoin(joins[0], allMatchers)) {
      const subSql = this._subselectSQL(joins[0], this._matchers, order, limit);
      return `SELECT${distinct} ${result} FROM \`${this._klass.name}\` WHERE \`id\` IN (${subSql}) ${order}`;
    }

    return `SELECT${distinct} ${result} FROM \`${this._klass.name}\` ${this._whereClause()} ${order} ${limit}`;
  }

  // If one of our matchers requires a join, and the attribute configuration lists
  // all of the other order and matcher attributes in \`joinQueryableBy\`, it means
  // we can make the entire WHERE and ORDER BY on a sub-query, which improves
  // performance considerably vs. finding all results from the join table and then
  // doing the ordering after pulling the results in the main table.
  //
  // Note: This is currently only intended for use in the thread list
  //
  _canSubselectForJoin(matcher, allMatchers) {
    const joinAttribute = matcher.attribute();

    if (!Number.isInteger(this._range.limit)) {
      return false;
    }

    const allMatchersOnJoinTable = allMatchers.every((m) =>
      (m === matcher) || (joinAttribute.joinQueryableBy.includes(m.attr.modelKey)) || (m.attr.modelKey === 'id')
    );
    const allOrdersOnJoinTable = this._orders.every((o) =>
      (joinAttribute.joinQueryableBy.includes(o.attr.modelKey))
    );

    return (allMatchersOnJoinTable && allOrdersOnJoinTable);
  }

  _subselectSQL(returningMatcher, subselectMatchers, order, limit) {
    const returningAttribute = returningMatcher.attribute()

    const table = tableNameForJoin(this._klass, returningAttribute.itemClass);
    const wheres = subselectMatchers.map(c => c.whereSQL(this._klass)).filter(c => !!c);

    let innerSQL = `SELECT \`id\` FROM \`${table}\` WHERE ${wheres.join(' AND ')} ${order} ${limit}`;
    innerSQL = innerSQL.replace(new RegExp(`\`${this._klass.name}\``, 'g'), `\`${table}\``);
    innerSQL = innerSQL.replace(new RegExp(`\`${returningMatcher.joinTableRef()}\``, 'g'), `\`${table}\``);
    return innerSQL;
  }

  _whereClause() {
    const joins = [];
    this._matchers.forEach((c) => {
      const join = c.joinSQL(this._klass)
      if (join) {
        joins.push(join);
      }
    });

    this._includeJoinedData.forEach((attr) => {
      const join = attr.includeSQL(this._klass)
      if (join) {
        joins.push(join);
      }
    });

    const wheres = [];
    this._matchers.forEach(c => {
      const where = c.whereSQL(this._klass);
      if (where) {
        wheres.push(where)
      }
    });

    let sql = joins.join(' ')
    if (wheres.length > 0) {
      sql += ` WHERE ${wheres.join(' AND ')}`;
    }
    return sql;
  }

  _orderClause() {
    if (this._orders.length === 0) {
      return ''
    }

    let sql = ' ORDER BY '
    this._orders.forEach((sort) => {
      sql += sort.orderBySQL(this._klass);
    });
    return sql;
  }

  // Private: Marks the object as final, preventing any changes to the where
  // clauses, orders, etc.
  finalize() {
    if (this._finalized) {
      return this;
    }

    if (this._orders.length === 0) {
      const natural = this._klass.naturalSortOrder();
      if (natural) {
        this._orders.push(natural);
      }
    }

    if (this._returnOne && !this._range.limit) {
      this.limit(1);
    }

    this._finalized = true;
    return this;
  }

  // Private: Throws an exception if the query has been frozen.
  _assertNotFinalized() {
    if (this._finalized) {
      throw new Error(`ModelQuery: You cannot modify a query after calling \`then\` or \`listen\``);
    }
  }

  // Observables

  observe({allowQueryChanges = false, name = null} = {}) {
    return Rx.Observable.create((observer) => {
      const pool = this._database._subscriptionPool;
      let unsubscribe = null;
      if (allowQueryChanges) {
        unsubscribe = pool.addPrivateSubscription(name, this, (v) => observer.onNext(v));
      } else {
        unsubscribe = pool.add(this, (v) => observer.onNext(v));
      }
      return Rx.Disposable.create(unsubscribe);
    });
  }

  // Introspection
  // (These are here to make specs easy)

  matchers() {
    return this._matchers;
  }

  matchersFlattened() {
    const all = []
    const traverse = (matchers) => {
      if (!(matchers instanceof Array)) {
        return;
      }
      for (const m of matchers) {
        if (m.children) {
          traverse(m.children);
        } else {
          all.push(m);
        }
      }
    }
    traverse(this._matchers);
    return all;
  }

  matcherValueForModelKey(key) {
    const matcher = this._matchers.find(m => m.attr.modelKey === key)
    return matcher ? matcher.val : null;
  }

  range() {
    return this._range;
  }

  orderSortDescriptors() {
    return this._orders;
  }

  objectClass() {
    return this._klass.name;
  }
}

description:
ModelQuery exposes an ActiveRecord-style syntax for building database queries
that return models and model counts. Model queries are returned from the factory methods
{RxDatabase::find}, {RxDatabase::findBy}, {RxDatabase::findAll},
and {RxDatabase::count}, and are the primary interface for retrieving data
from the app's local cache.

ModelQuery does not allow you to modify the local cache. To create, update or
delete items from the local cache, see {RxDatabase::inTransaction}
and {DatabaseTransaction::persistModel}.

*Simple Example:** Fetch a thread

```js
const query = db.find(Thread, '123a2sc1ef4131');
query.then((thread) {
// thread or null
});
```

*Advanced Example:** Fetch 50 threads in the inbox, in descending order

```coffee
const query = db.findAll(Thread);
query.where([Thread.attributes.categories.contains('label-id')])
.order([Thread.attributes.lastMessageReceivedTimestamp.descending()])
.limit(100).offset(50)
.then((threads) {
// array of threads
});
```

